	public static void display(String s)
	{
		if (s.equals("walk"))
			displayAnim(walk);
		else if (s.equals("shoot"))
			displayAnim(shoot);
		else if (s.equals("no_arrow"))
			displayAnim(no_arrow);
		else if (s.equals("left"))
			displayAnim(left);
		else if (s.equals("right"))
			displayAnim(right);
		else if (s.equals("grab"))
			displayAnim(grab);
		else if (s.equals("no_gold"))
			displayAnim(no_gold);
		else if (s.equals("climb"))
			displayAnim(climb);
	}
	
	private static void displayAnim(String[] an)
	{
		for (int i = 0; i < an.length; i++)
		{
			System.out.print(an[i]);
			try
			{
				Thread.sleep(100);
			}
			catch (InterruptedException e)
			{
				
			}
		}
	}
